import React, { Component } from 'react'
import './style.scss'

export default class PlayrerResponse extends Component {
	render(){
		console.log('should animate out ', this.props.quizState === 'answer' && !this.props.correct)
		let cn = 'playerResponse'
		if (!this.props.response) cn += ' hidden'
		if (this.props.quizState === 'answer' && !this.props.correct) cn += ' animateOut'
			console.log('quiz state', this.props.quizState)
		return(
			<div className={cn}>
				<p className="playerResponseName">{this.props.name}</p>
				{this.props.bonus && 
					<p className="bonus" >+50</p>
				}
				{this.props.correct && this.props.quizState === 'answer' &&
					<p className="correct">+100</p>
				}	
			</div>
		)
	}
}
