import React, { Component } from 'react'
import { push } from 'react-router-redux'
import { bindActionCreators } from 'redux'
import { connect } from 'react-redux'
import io from 'socket.io-client';


//pingP events
import {subscribeToPlayerEvents} from 'sockets/player'
import {subscribeToHostEvents} from 'sockets/host'

//pingP actions
import { setRoomCode, addPlayer, startRoundHost, setResponses, setCoords1, setCoords2 } from 'actions/PingP/host'
import { setPlayerRoom, setPlayerNumber, powerUpGained, powerUpUsed } from 'actions/PingP/player'
import { setSelf } from 'actions/global/player'
import { startGame, setGameOver, powerUpUsedGameplay } from 'actions/PingP/gameplay'
import { updatePlayer } from 'actions/general/host'

class SocketListener extends Component {
  constructor(props){
    super(props)
  }

  componentDidMount(){
    // console.log('socket listener mounting')
    if (this.props.isHost){
      subscribeToHostEvents(this, (action, data) => {
        this.props[action](data)
      })
    } else {
     subscribeToPlayerEvents(this, (action, data) => {
        this.props[action](data)
     })
   }
  }

  render(){
    return(
      <div>
      </div>
    )
  }
}

const mapStateToProps = state => ({
  // count: state.counter.count
})

const mapDispatchToProps = dispatch => bindActionCreators({
  //general functions
  setSelf,
  //game specific functions
  setRoomCode,
  powerUpUsedGameplay,
  powerUpGained,
  powerUpUsed,
  startGame,
  setGameOver,
  setPlayerNumber,
  setResponses,
  setCoords1,
  setCoords2,
  startRoundHost,
  addPlayer,
  setPlayerRoom,
  push: (path) => push('/' + path)
}, dispatch)

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(SocketListener)
