const initialState = {
  question: null,
  waiting: true,
}

export default (state = initialState, action) => {
  switch (action.type) {

    case 'QUIZ_SHOW_QUESTION_PLAYER':
      //handles the end of the quiz  
      return {
        ...state,
        question: action.payload
      }
    case 'QUIZ_SET_WAITING_PLAYER':
      //handles the end of the quiz  
      return {
        ...state,
        waiting: action.payload
      }
    default:
      return state
  }
}
