import React, { Component } from 'react'
import { push } from 'connected-react-router'
import { bindActionCreators } from 'redux'
import { connect } from 'react-redux'
import { setupGame, showAnswer, showScores } from 'actions/quiz/host'
import SocketListener from 'containers/SocketListener/Quiz'
import { showQuestionSocket } from 'sockets/Quiz/host'
import { showQuestionHost } from 'actions/quiz/host'
import './style.scss'
import Question from './Question'
import PlayerResponse from './PlayerResponse'
class QuizHost extends Component {

	constructor(props){
		super(props)
		this.timeouts = {

		}
		this.state = {

		}
	}

	componentWillMount(){
		this.setupGame()
	}

	getNumberOfTruthies(array){
		let count = 0
		for (var i = 0; i < array.length; i++){
			if(array[i]) count += 1
		}
		return count
	}



	compareResponsesToPlayers(){
		let playerCount = 0;
		let players = this.props.players.length;
		let responses = this.props.responses
		let allPlayersHaveResponded = true;
		for (var i = 0; i < players.length; i++){
			if (players[i]){
				let hasResponded = responses.find(r => r.id === players[i].id)
				if (!hasResponded){
					allPlayersHaveResponded = false
				}
			}
		}
		if (allPlayersHaveResponded){
			this.calculateScores()
		}
	}

	componentWillReceiveProps(np){
		if (this.props.responses.length !== np.responses.length){
			//check to see if all active players have responded
			this.compareResponsesToPlayers()
		}
	}

	shuffle(array) {
	  var currentIndex = array.length, temporaryValue, randomIndex;
	  while (0 !== currentIndex) {
	    randomIndex = Math.floor(Math.random() * currentIndex);
	    currentIndex -= 1;
	    temporaryValue = array[currentIndex];
	    array[currentIndex] = array[randomIndex];
	    array[randomIndex] = temporaryValue;
	  }
	  return array;
	}

	setupGame(){
		fetch('https://opentdb.com/api.php?amount=10&type=multiple')
			.then((resp) => resp.json()) // Transform the data into json
			.then((data) => {
			// give players scores
			// let playersWithScores = []
			// for (let i = 0; i < this.props.players.length; i++){
			// 	let player = {
			// 		id: this.props.players[i].id,
			// 		name: this.props.players[i].name,
			// 		score: 0
			// 	}
			// 	if (this.props.player[i]){
			// 		playersWithScores.push(player)
			// 	}
			// }
			//shuffle wrong answers with correct ones
			for (let i = 0; i < data.results.length; i++){
				let q = data.results[i]
				q.all_answers = q.incorrect_answers
				q.all_answers.push(q.correct_answer) 
				q.all_answers = this.shuffle(q.all_answers)
				q.correct_index = q.all_answers.indexOf(q.correct_answer)
			}
			console.log(q)
			let obj = {
				questions: data.results,
				// players: playersWithScores
			}
			console.log(obj)
			this.props.setupGame(obj)
			this.intro()
		})
		.catch((err) => {
			console.log('error fetchi9ng data', err)
		})
	}

	intro(){	
		this.showQuestion()
	}

	showQuestion(){
		console.log(this.props)
		showQuestionSocket(this, this.props.socket)
	}

	showAnswer(){
		this.props.showAnswer()
	}

	calculateScores(){


		let players = this.props.players
		let responses = this.props.responses 
		let scores = this.props.scores 
		let startTime = this.props.startTime



	}

	showScores(){
		this.props.showScores()
		//move to next question index.
		//reset timings
		//reset responses
	}







	render(){
		const { currentQuestion } = this.props

		return(
			<div className="quizHostContainer">
			<SocketListener isHost socket={this.props.socket} />
				{this.props.quizState === 'break' &&
					<p>Break</p>
				}
				{this.props.quizState === 'question' &&
					<div className="sceneContainer">
					<Question question={currentQuestion}/>
					<div className="responsesContainerHost">
						{this.props.responses && this.props.responses.map((response, i) => {
							return(
								<PlayerResponse {...response}/>
							)
						})}
					</div>
					</div>
				}
				{this.props.quizState === 'answer' &&
					<p>Answer</p>
				}
				{this.props.quizState === 'end' &&
					<p>End</p>
				}
			</div>
		)
	}
}


const mapStateToProps = state => ({
	room: state.generalHost.room,
	startTime: state.quizHost.startTime,
	players: state.generalHost.players,
	router: state.router,
	responses: state.quizHost.responses,
	quizPlayers: state.quizHost.players,
	currentQuestion: state.quizHost.currentQuestion,
	quizState: state.quizHost.quizState,

})

const mapDispatchToProps = dispatch => bindActionCreators({
  push: (path) => push(path),
  setupGame,
  showAnswer,
  showQuestionHost,
  showScores,
}, dispatch)

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(QuizHost)
