import React, { Component } from 'react'
import { bindActionCreators } from 'redux'
import { connect } from 'react-redux'
import InfoBar from 'components/MobileInfoBar'
import './style.scss'
import games from 'config/games'
import Button from 'components/Button'
class MobileHome extends Component {

	onClick(){

	}
	render(){
		const { roomCode, name, playerNumber } = this.props
		return(
			<div className="mobileHomeContainer">
				<InfoBar player={roomCode} name={name} playerNumber={playerNumber}/>
				{games && games.map((game, i) => {
					return(
						<Button onClick={this.onClick.bind(this, game.slug)} text={game.name} />
					)
				})}
			</div>
		)
	}
}

const mapStateToProps = state => ({
	roomCode: state.generalPlayer ? state.generalPlayer.room : '',
	name: state.generalPlayer ? state.generalPlayer.name: '',
	playerNumber: state.generalPlayer ? state.generalPlayer.playerNumber : '',
	router: state.router
})

const mapDispatchToProps = dispatch => bindActionCreators({

}, dispatch)

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(MobileHome)
