import React, { Component } from 'react'
import { push } from 'connected-react-router'
import { bindActionCreators } from 'redux'
import { connect } from 'react-redux'
import localSetup from 'config/local'
import SocketListener from 'containers/SocketListener'
import { joinRoom } from 'sockets/player'
import { setSelf } from 'actions/global/player'
import './style.scss'

class MobileConnection extends Component {

	constructor(props){
		super(props)
		this.state = {
			code: this.props.match.params.code || 'adas',
			name: ''
		}
	}

	componentWillMount(){
	console.log(this) 
		let code = this.props.match.params.code
		if (code){
			let obj = {
		      name: this.state.name,
		      room: this.state.code
		    }
		    // joinRoom(obj, this)
		}
	}


	render(){ 
		return(
			<div className="mobileConnectionContainer">
				<SocketListener />
				<input type="text" value={this.state.code} />
				<input type="name" value={this.state.name} />
			</div>
		)
	}
}



const mapStateToProps = state => ({
	roomCode: state.host.roomCode,
	players: state.host.players,
	router: state.router
})

const mapDispatchToProps = dispatch => bindActionCreators({
  push: (path) => push(path),
  setSelf
}, dispatch)

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(MobileConnection)
