import React, { Component } from 'react'
import { push } from 'connected-react-router'
import { bindActionCreators } from 'redux'
import { connect } from 'react-redux'
import localSetup from 'config/local'
import SocketListener from 'containers/SocketListener'
import { joinRoom } from 'sockets/player'
import { setSelf } from 'actions/global/player'
import Button from 'components/Button'
import TextInput from 'components/TextInput'

import './style.scss'

class MobileConnection extends Component {

	constructor(props){
		super(props)
		this.state = {
			code: this.props.match.params.code || '',
			name: ''
		}
	}

	componentWillMount(){
	console.log(this) 
		let code = this.props.match.params.code
		if (code){
			let obj = {
		      name: this.state.name,
		      room: this.state.code
		    }
		    // joinRoom(obj, this)
		}
	}

	onChange(nothing, key, e){
		this.setState({
			[key]: e.target.value
		})
	}


	render(){ 
		return(
			<div className="mobileConnectionContainer">
				<SocketListener />
				<div className="mobileFormContainer">
					<TextInput label="Room code" name="code" value={this.state.code} onChange={this.onChange.bind(this, 'code')}/>
					<TextInput label="Name" name="name"value={this.state.name} onChange={this.onChange.bind(this, 'name')}/>
					<Button text="Let's go" />
				</div>

			</div>
		)
	}
}



const mapStateToProps = state => ({
	roomCode: state.host.roomCode,
	players: state.host.players,
	router: state.router
})

const mapDispatchToProps = dispatch => bindActionCreators({
  push: (path) => push(path),
  setSelf
}, dispatch)

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(MobileConnection)
