import React, { Component } from 'react'
import './style.scss'

export default class PlayrerResponse extends Component {
	render(){
		let cn = 'playerResponse'
		if (!this.props.response) cn += ' hidden'
		if (this.props.bonus) cn += ' bonus'
		if (this.props.quizState === 'answer' && !this.props.correct){
			cn += ' animateOut'
		} else if (this.props.quizState === 'answer'){
			cn += ' correct'
		} 
		return(
			<div className={this.props.response ? "playerResponse" : 'playerResponse hidden'}>
				<p className="playerResponseName">{this.props.name}</p>
			</div>
		)
	}
}
