import React, { Component } from 'react'
import { push } from 'connected-react-router'
import { bindActionCreators } from 'redux'
import { connect } from 'react-redux'
import { setupGame } from 'actions/quiz'
import './style.scss'
import Question from './Question'
class QuizHost extends Component {

	constructor(props){
		super(props)
		this.timeouts = {

		}
		this.state = {

		}
	}

	componentWillMount(){
		this.setupGame()
	}

	setupGame(){
		fetch('https://opentdb.com/api.php?amount=10&type=multiple')
		.then((resp) => resp.json()) // Transform the data into json
		.then((data) => {
			console.log('got data', data)
			let playersWithScores = []
			// if (this.props.players.length){
				for (var i = 0; i < this.props.players.length; i++){
					let player = {
						id: this.props.players[i].id,
						name: this.props.players[i].name,
						score: 0
					}
					playersWithScores.push(player)
				}
				let obj = {
					questions: data.results,
					players: playersWithScores
				}
				console.log('calling setup')
				this.props.setupGame(obj)
			// } else {
			// 	this.props.push('/h/')
			// }
		})
	}



	render(){
		console.log(this.props)
		const { currentQuestion } = this.props
		return(
			<div className="quizHostContainer">
				{this.props.quizState === 'break' &&
					<p>Break</p>
				}
				{this.props.quizState === 'question' &&
					<Question question={currentQuestion}/>
				}
				{this.props.quizState === 'answer' &&
					<p>Answer</p>
				}
				{this.props.quizState === 'end' &&
					<p>End</p>
				}
			</div>
		)
	}
}


const mapStateToProps = state => ({
	roomCode: state.generalHost.room,
	players: state.generalHost.players,
	router: state.router,
	quizPlayers: state.quiz.players,
	currentQuestion: state.quiz.currentQuestion,
	quizState: state.quiz.quizState,

})

const mapDispatchToProps = dispatch => bindActionCreators({
  push: (path) => push(path),
  setupGame
}, dispatch)

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(QuizHost)
