  return dispatch => {
    dispatch({
      type: 'QUIZ_SHOW_ANSWERS',
      payload
    })
  }
}

export const showScores = (payload) => {
  return dispatch => {
    dispatch({
      type: 'QUIZ_SHOW_SCORES',
      payload
    })
  }
}
