import React, { Component } from 'react'
import { bindActionCreators } from 'redux'
import { connect } from 'react-redux'
import localSetup from 'config/local'
import './style.scss'

class HostHome extends Component {
	render(){
		let QRuri = false;
		if (this.props.roomCode){
			QRuri = localSetup.isLocal ? 'https://api.qrserver.com/v1/create-qr-code/?size=250x250&data=http://' + localSetup.localServer + ':3000/m/' + this.props.roomCode.toLowerCase() :
			'https://api.qrserver.com/v1/create-qr-code/?size=250x250&data=' +  encodeURIComponent( localSetup.domain + '/m/' + this.props.roomCode.toLowerCase()) 
		} 
		return(
			<div className="hostHomeContainer">
				<div className="players">
					{this.props.players.map((player, i) => {
						return(
							<div className="hostPlayercontainer">
								{player.name || 'Ted'}
							</div>
						)
					})}
				</div>
				<div className="qr">
					<img src={QRuri} className="globalQRCode" />
				</div>

			</div>
		)
	}
}


const mapStateToProps = state => ({
	roomCode: state.host.roomCode,
	players: state.generalHost.players,
	router: state.router
})

const mapDispatchToProps = dispatch => bindActionCreators({

}, dispatch)

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(HostHome)
