const initialState = {
  questions: [],
  questionIndex: 0,
  players: [],
  currentQuestion: null,
  quizState: 'question'
}

export default (state = initialState, action) => {
  switch (action.type) {
    case 'QUIZ_SETUP_GAME':
    console.log('setting up game reducer')
      return {
        ...state,
        questions: action.payload.questions,
        players: action.payload.players,
        currentQuestion: action.payload.questions[0],
        questionIndex: 0,
      }
    default:
      return state
  }
}
